mapper.select:
select * from shopping_cart

    and user_id = #{userId}
    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and dish_flavor = #{dishFlavor}

mapper.update:
update shopping_cart

    dish_flavor  = #{dishFlavor}
    number  = #{number}



    and user_id = #{userId}
    and id = #{id}
    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}
